<span class="line"><span style="color:#C792EA;">var</span><span style="color:#A6ACCD;"> colors3 </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> colors</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">slice</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">4</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
